mapper.insert:
INSERT INTO users(u_name, u_password
, u_workplace
, u_address
, u_tel
) VALUES (#{uName}, #{uPassword}
, #{uWorkplace}
, #{uAddress}
, #{uTel}
)

mapper.update:
UPDATE users SET u_name = #{uName},
u_password = #{uPassword}, u_workplace = #{uWorkplace},
u_address = #{uAddress}, u_tel = #{uTel}
WHERE u_name = #{uName}
